import React, { useState } from "react";
import "./Form.css";

const Form = ({ setShowMeme }) => {
  const [topText, setTopText] = useState("");
  const [bottomText, setBottomText] = useState("");

  const handleTopTextChange = (e) => {
    setTopText(e.target.value);
  };

  const handleBottomTextChange = (e) => {
    setBottomText(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch("https://api.imgflip.com/caption_image",{
      method:"POST",
      username:"rohan04",
      password:"rohanmathur",
      text0:"abc",
      text1:"def",
      font:"impact",
      max_font_size:"50"
    }).then((response) => response.json()).then((data) => {
      console.log(data)
    })
  };

  const goBack = () => {
    setShowMeme(true);
  };

  return (
    <div>
      <h2>Generate your own meme!</h2>
      <button className="secondary-btn" onClick={goBack}>
        Go Back!
      </button>
      <button className="primary-btn" onClick={handleSubmit}>
        Generate
      </button>
      <div className="container">
        <input onChange={handleTopTextChange} value={topText}></input>
        <input onChange={handleBottomTextChange} value={bottomText}></input>
      </div>
    </div>
  );
};

export default Form;
