import React, {useState} from "react";
import "./Form.css";

const Form = ({setShowMeme}) => {
  const [topText, setTopText] = useState("")
  const [bottomText, setBottomText] = useState("")

  const handleTopTextChange = (e) => {
    console.log(e.target.value)
    setTopText(e.target.value)
  }

  const handleBottomTextChange = (e) => {
    console.log(e.target.value)
    setBottomText(e.target.value)
  }

  const goBack = () => {
    setShowMeme(true)
  }

  return (
    <div>
      <h2>Generate your own meme!</h2>
      <button className="secondary-btn" onClick={goBack}>Go Back!</button>
      <button className="primary-btn">Generate</button>
      <div className="container">
        <input onChange={handleTopTextChange} value={topText}></input>
        <input onChange={handleBottomTextChange} value={bottomText}></input>
      </div>
    </div>
  )
};

export default Form;
